mapper.update:
update cms_emailmaininfo set email=#{email},estatus=#{eStatus},erole=#{eRole},ehost=#{eHost},epassword=#{ePassword},enickName=#{nickName},
 Modifydate=TO_DATE(#{modifyDate},'yyyy-mm-dd hh24:mi:ss'),modifyUser=#{modifyUser}
 where id=#{id}

mapper.select:
select (select emailjobname from cms_emailjobdef where emailjobcode = s.type) emailJobName, s.theme, s.content, to_char(s.sendtime,'yyyy-mm-dd hh24:mi:ss') sendTime, s.sender emailSender, s.addressee emailReceiver, s.copyperson emailCopype, (case s.state when '1' then '成功' when '0' then '失败' end) sendState, s.errMsg from cms_emailsendlog s where 1=1
AND s.type = #{emailJobCode}
AND TO_DATE(to_char(s.SENDTIME,'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss') >= TO_DATE(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND TO_DATE(to_char(S.SENDTIME,'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss') <= TO_DATE(#{endDate},'yyyy-mm-dd hh24:mi:ss')
AND S.state = #{sendState}
order by s.sendtime desc

select count(*) from cms_emailsendlog s where 1=1
AND s.type = #{emailJobCode}
AND TO_DATE(to_char(s.SENDTIME,'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss') >= TO_DATE(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND TO_DATE(to_char(S.SENDTIME,'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss') <= TO_DATE(#{endDate},'yyyy-mm-dd hh24:mi:ss')
AND S.state = #{sendState}
